// override default option , container , control style
export const Styles = {
    container: (provided) => ({
        ...provided,
        margin: "1rem auto"

    }),
    option: (provided, state) => ({
        ...provided,
        borderBottom: '1px solid #cccccc',
        padding: 4,
        backgroundColor: state.isSelected ? 'white' : 'white',
        color: state.isSelected ? 'black' : 'black'

    }),
    control: (provided, state) => ({
        ...provided,

    })
}
