import React, { useState } from 'react'
import { Button, Col } from 'reactstrap';
import EditLayout from "./Layout/EditLayout";
import Default from "./Layout/Default";

const EditAction = (props) => {

    const [edit, setEdit] = useState(false);
    const {
        buttonLabel,
        label,
        photo,
        value
    } = props
    const toggle = () => setEdit(!edit);

    return (
        <React.Fragment>
            {!edit ? Default(photo, label, value) : EditLayout(photo, label)}
            <Col xs="0" sm="0">
                <Button color="primary" onClick={toggle}>{!edit ? buttonLabel : "Simpan"}</Button>{" "}
            </Col>
        </React.Fragment>
    )
}


export default EditAction
