import React, { Component } from "react";
import AsyncSelect from "react-select/async";
import SingleValueLayout from "./Layout/SingleValueLayout";
import FormatOptionLabel from "./FormatOptionLabel";
import { Container } from 'reactstrap';
import { Styles } from "./Styles/Styles";

class Home extends Component {
    state = {
        userData: []
    };

    // fetch 50 user from randomuser and push it to userData state.
    componentDidMount() {
        const getUserUrl = "https://randomuser.me/api/?results=50";
        fetch(getUserUrl)
            .then(response => response.json())
            .then(response => this.setState({
                userData:
                    response.results.map(
                        data => (
                            {
                                label: `${data.name.first} ${data.name.last}`,
                                value: `${data.name.first} ${data.name.last}`,
                                photo: data.picture.thumbnail
                            }
                        )
                    )
            })
            );
    }

    // filter user by comparing label and user input
    filterName = inputValue => {
        return this.state.userData.filter(i =>
            i.label.toLowerCase().includes(inputValue.toLowerCase())
        );
    };
    // async action every 0.5 second to check the input value
    promiseOptions = inputValue =>
        new Promise(resolve => {
            setTimeout(() => {
                resolve(this.filterName(inputValue));
            }, 500);
        });

    render() {
        const { userData } = this.state
        return (
            <Container>
                <AsyncSelect
                    cacheOptions
                    menuIsOpen={true}
                    closeMenuOnSelect={false}
                    components={{ SingleValue: SingleValueLayout }}
                    styles={Styles}
                    defaultOptions={userData}
                    placeholder={"Pilih personalia..."}
                    formatOptionLabel={FormatOptionLabel}
                    loadOptions={this.promiseOptions}
                />
            </Container>
        );
    }
}


export default Home;
