import React, { useState } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

const DeleteLayout = (props) => {
    const {
        buttonLabel,
        className
    } = props;

    const [modal, setModal] = useState(false);

    const toggle = () => setModal(!modal);

    return (
        <React.Fragment>
            <Button color="danger" onClick={toggle}>{buttonLabel}</Button>
            <Modal isOpen={modal} toggle={toggle} className={className}>
                <ModalHeader toggle={toggle}>Delete Personalia</ModalHeader>
                <ModalBody>
                    Yakin ingin menghapus personalia ini ?
                </ModalBody>
                <ModalFooter>
                    <Button color="primary" onClick={toggle}>Ya</Button>{' '}
                    <Button color="secondary" onClick={toggle}>Tidak</Button>
                </ModalFooter>
            </Modal>
        </React.Fragment>
    );
}

export default DeleteLayout;
