import React, { Component } from "react";
import AsyncSelect from "react-select/async";
import { Container } from 'reactstrap';

class Home extends Component {
    state = {
        userData: []
    };

        //Memasukan data dari random user
        componentDidMount() {
            const getUserUrl = "https://randomuser.me/api/?results=50"; //data yg dimasukan sebanyak 50(result=50)
            
            fetch(getUserUrl)
                .then(response => response.json())
                .then(response => this.setState({
                userData : response.results.map(
                    data => ({
                        label: `${data.name.first} ${data.name.last}`,
                        value: `${data.name.first} ${data.name.last}`,
                        photo: data.picture.thumbnail
                    })
                )
                })
            );
        }

        //Search nama personalia
        searchName = (inputValue)=>{
            return this.state.userData.filter(
                i=>i.label.toLowerCase().includes(inputValue.toLowerCase())
            );
        };

        //Mengecek inputan
        promiseOptions = (inputValue)=>new Promise(resolve => {
                setTimeout(() => {
                    resolve(this.searchName(inputValue));
                }, 50);
            });

        render() {
            const { userData } = this.state
            return (
                <Container>
                    <AsyncSelect
                        menuIsOpen={true} //menampilkan data personalia
                        closeMenuOnSelect={true} //memasukan pencarian
                        defaultOptions={userData}
                        loadOptions={this.promiseOptions}
                    />
                </Container>
            );
        }
    }


export default Home;
